import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom"

export default function EditBook(){
       const {bookid} = useParams();
       const [name, setName] = useState('');
       const [author, setAuthor] = useState('');
       const [category, setCategory] = useState('');
       const [isbn, setIsbn] = useState('');
    //    const navigate = useNavigate();
       const [validation, setValidation] = useState(false)
       useEffect(() => {
        fetch("http://localhost:5001/books/" + bookid)
        .then((res) => res.json())
        .then((data) => console.Console.log(data))
        .catch((err) => console.log(err.message))
       },[]);
       const handleSubmit = () => {


    }
    return(
        <div className="container">
            <h2>Update book details</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name: </label>
                <input type="text" id="name" name="name" required value={name} onChange = {e => setName(e.target.value)} onMouseDown = {() => setValidation(true)}/>
                {name.length === 0 && validation && <span className="errorMsg">Please enter your name</span>}

                <label htmlFor="author">Author: </label>
                <input type="text" id="author" name="author" required value={author} onChange = {e => setAuthor(e.target.value)} onMouseDown = {() => setValidation(true)}/>
                {author.length === 0 && validation && <span className="errorMsg">Please enter your author</span>}

                <label htmlFor="category">Category: </label>
                <input type="text" id="category" name="category" required value={category} onChange = {e => setCategory(e.target.value)} onMouseDown = {() => setValidation(true)}/>
                {category.length === 0 && validation && <span className="errorMsg">Please enter your category</span>}

                <label htmlFor="c">ISBN: </label>
                <input type="text" id="isbn" name="isbn" required value={isbn} onChange = {e => setIsbn(e.target.value)} onMouseDown = {() => setValidation(true)}/>
                {isbn.length === 0 && validation && <span className="errorMsg">Please enter your isbn</span>}

                <div>
                    <button className="btn btn-primary">Update</button>
                    <Link to="/" className="btn btn-primary">Back</Link>
                </div>
            </form>
        </div>
    )
}
