import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function CreateBook(){
    const [name, setName] = useState('');
    const [author, setAuthor] = useState('');
    const [category, setCategory] = useState('');
    const [isbn, setIsbn] = useState(0);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        const bookData = { name, author, category, isbn };
        console.log(bookData);

        fetch("http://localhost:5001/books",{
            method: 'POST',
            headers: {
                "content-type":"application/json"
            },
            body: JSON.stringify(bookData)
        })
        .then((res) => {
            alert("Book Data saved successfully")
            navigate("/");
        })
        .catch((err) => console.log(err.message)
    )
    }
    return(
        <div className="container">
            <h2>Add new Book</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name: </label>
                <input type="text" id="name" name="name" required value={name} onChange = {e => setName(e.target.value)}/>
                {name.length === 0 && <span className="errorMsg">Please enter your name</span>}

                <label htmlFor="author">Author: </label>
                <input type="text" id="author" name="author" required value={author} onChange = {e => setAuthor(e.target.value)}/>
                {author.length === 0 && <span className="errorMsg">Please enter your author</span>}

                <label htmlFor="category">Category: </label>
                <input type="text" id="category" name="category" required value={category} onChange = {e => setCategory(e.target.value)}/>
                {category.length === 0 && <span className="errorMsg">Please enter your category</span>}

                <label htmlFor="c">ISBN: </label>
                <input type="number" id="isbn" name="isbn" required value={isbn} onChange = {e => setIsbn(e.target.value)}/>
                {isbn.length !== 0 && <span className="errorMsg">Please enter your isbn</span>}

                <div>
                    <button className="btn btn-primary">Save</button>
                    <Link to="/" className="btn btn-primary">Back</Link>
                </div>
            </form>
        </div>
    )
}
