import { useState } from "react";
import { Link } from "react-router-dom";

export default function CreateBook(){
    const [name, setName] = useState('');
    const [author, setAuthor] = useState('');
    const [category, setCategory] = useState('');
    const [isbn, setIsbn] = useState(0);

    return(
        <div className="container">
            <h2>Add new Book</h2>
            <form>
                <label htmlFor="name">Name: </label>
                <input type="text" id="name" name="name" value={name} onChange = {e => setName(e.target.value)}/>

                <label htmlFor="author">Author: </label>
                <input type="text" id="author" name="author" value={author} onChange = {e => setAuthor(e.target.value)}/>

                <label htmlFor="category">Category: </label>
                <input type="text" id="category" name="category" value={category} onChange = {e => setCategory(e.target.value)}/>

                <label htmlFor="c">ISBN: </label>
                <input type="number" id="isbn" name="isbn" value={isbn} onChange = {e => setIsbn(e.target.value)}/>

                <div>
                    <button className="btn btn-primary">Save</button>
                    <Link to="/" className="btn btn-primary">Back</Link>
                </div>
            </form>
        </div>
    )
}
